        move_head(characterId) {
            var head = document.querySelector("#" + characterId + " .emoji_head");

            var tl = gsap.timeline({
                defaults: { duration: 0.5, ease: "power1.inOut" }
            });

            tl.to(head, { rotationY: -30, ease: "power2.in" })
                .to(head, { rotationY: 30, ease: "none" })
                .to(head, { rotationY: -30, ease: "none" })
                .to(head, { rotationY: 30, ease: "none" })
                .to(head, { rotationY: -30, ease: "none" })
                .to(head, { rotationY: 30, ease: "none" })
                .to(head, { rotationY: 0, ease: "power2.out" });
